interface IZKPVerifier {
    /**
     * @dev submitZKPResponse
     */
    function submitZKPResponse(
        uint64 requestId,
        uint256[] memory inputs,
        uint256[2] memory a,
        uint256[2][2] memory b,
        uint256[2] memory c
    ) external returns (bool);

    /**
     * @dev setZKPRequest
     */
